```js
"use strict";

class SessionController {
  create({ view }) {
    return view.render("session.create");
  }

  async store({ auth, request, response, session }) {
    const { email, password } = request.all();

    try {
      await auth.attempt(email, password);
    } catch (e) {
      session.flashExcept(["password"]);

